mapper.insert:
insert into
	reply_tb(
		reply_nm
		,content_nm
		,reply
		,replyer
) values (
	reply_seq.nextval
	,#{content_nm}
	,#{reply}
	,#{replyer}
)

mapper.update:
update
	reply_tb
set
	reply = #{reply}
where
	reply_nm = #{reply_nm}
and
	replyer = #{replyer}

mapper.delete:
delete from
	reply_tb
where
	reply_nm = #{reply_nm}
and
	replyer = #{replyer}
